function createBatch(Transaction[] memory txs) public {
    require(txs.length > 0, "Empty batch");
    bytes32 batchHash = keccak256(abi.encode(txs));
    batches[batchHash] = BatchState.Pending;
    emit BatchCreated(batchHash, txs.length);
}
