const tx = {
    from: wallet.address,
    to: contractAddress,
    data: encodedData,
    value: ethers.parseEther('0.1')
};

const receipt = await wallet.sendTransaction(tx);

const batch = await sequencer.createBatch([
    tx1,
    tx2,
    tx3
]);

await sequencer.processBatch(batch);

function createBatch(Transaction[] memory txs) public {
    require(txs.length > 0, "Empty batch");
    bytes32 batchHash = keccak256(abi.encode(txs));
    batches[batchHash] = BatchState.Pending;
    emit BatchCreated(batchHash, txs.length);
}
